import cv2 as cvimg = cv.imread("imgs/babe.jpg")# Laplacian Pyramid - difference between the layers in Gaussian pyramid levels# Gaussian Pyramid - repeat filtering and sub-sampling# ret = cv.pyrDown(img)# gaussian pyramid formationlayer = img.copy()gp = [layer]for i in range(6):    layer = cv.pyrDown(layer)    gp.append(layer)    # cv.imshow(str(i), layer)#laplacian pyramid formationlayer = gp[5]cv.imshow('upper level Gaus Pyr', layer)for i in range(5, 0, -1):    sh = gp[i-1].shape    gaussian_extended = cv.pyrUp(gp[i], dstsize=(sh[1], sh[0]))    laplacian = cv.subtract(gp[i-1], gaussian_extended)    cv.imshow(str(i), laplacian)# cv.imshow("img", img)cv.waitKey(0)cv.destroyAllWindows()